sys_variable_value.value:
(function parse(inputs, outputs) {


 var item = JSON.parse(inputs.response_body);
 outputs.targetObject.id = item.glacier_id;
 outputs.targetObject.name = item.name;
 outputs.targetObject.country = item.country;
 outputs.targetObject.latitude = item.latitude;
 outputs.targetObject.longitude = item.longitude;



})(inputs, outputs);

